import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { HttpClientModule } from '@angular/common/http';
import { RouterModule } from '@angular/router';
import { SongCardComponent } from './components/song-card/song-card.component';
import { FilterBarComponent, FilterState } from './components/filter-bar/filter-bar.component';
import { StatsPanelComponent } from './components/stats-panel/stats-panel.component';
import { Song } from './models/song';
import { SongService } from './services/song.service';

@Component({
  selector: 'app-songs',
  standalone: true,
  imports: [CommonModule, HttpClientModule, RouterModule, SongCardComponent, FilterBarComponent, StatsPanelComponent],
  templateUrl: './songs.component.html',
  styleUrl: './songs.component.scss',
})
export class SongsComponent implements OnInit {
  title = 'Top 100 of 2025';
  songs: Song[] = [];
  allGenres: string[] = [];
  filters: FilterState = { search: '', genre: '', type: 'all', sort: 'rank', order: 'asc' };
  genres: string[] = [];
  loading = false;
  error = '';
  selectedSong?: Song;
  private previewAudio?: HTMLAudioElement;

  constructor(private readonly songService: SongService) {}

  ngOnInit(): void {
    this.loadSongs(true);
  }

  handleFiltersChange(filters: FilterState): void {
    this.filters = { ...filters };
    this.loadSongs();
  }

  loadSongs(updateGenres = false): void {
    this.loading = true;
    this.error = '';
    const query = {
      ...this.filters,
      type: this.filters.type === 'all' || this.filters.type === 'collabduo' ? undefined : this.filters.type,
    };
    this.songService.getSongs(query).subscribe({
      next: (items) => {
        let result = items;
        if (this.filters.type === 'collabduo') {
          result = items.filter((s) => {
            const t = (s.type || '').toLowerCase();
            return t === 'collab' || t === 'duo';
          });
        }
        this.songs = this.filters.order === 'desc' ? [...result].reverse() : result;
        if (updateGenres || this.genres.length === 0) {
          this.allGenres = items.map((s) => (s.genre || '').toLowerCase()).filter(Boolean);
          this.genres = Array.from(new Set(this.allGenres)).sort();
        }
        this.loading = false;
      },
      error: () => {
        this.error = 'Failed to load songs. Is the backend running on http://localhost:4000?';
        this.loading = false;
      },
    });
  }

  selectSong(song: Song): void {
    this.selectedSong = song;
  }

  previewSong(song: Song): void {
    if (!song.previewUrl) return;
    if (this.previewAudio) {
      this.previewAudio.pause();
    }
    this.previewAudio = new Audio(song.previewUrl);
    this.previewAudio.play().catch(() => {
      this.error = 'Unable to play preview.';
    });
  }
}
<main class="page">
  <header class="hero">
    <div>
      <h1>Top 100 Songs of 2025</h1>
      <p class="lede">Browse and filter the top songs of 2025.</p>
      <div class="hero-actions">
        <a class="ghost" routerLink="/">Home</a>
        <button class="primary" (click)="loadSongs()">Refresh list</button>
        <button class="ghost" (click)="filters = { search: '', genre: '', type: 'all', sort: 'rank' }; loadSongs();">
          Reset filters
        </button>
      </div>
    </div>
    <div class="hero-card">
      <div class="stat">
        <div class="label">Loaded</div>
        <div class="value">{{ songs.length }}</div>
      </div>
      <div class="stat">
        <div class="label">Filter</div>
        <div class="value">{{ filters.genre || 'All genres' }}</div>
      </div>
      <div class="stat">
        <div class="label">Type</div>
        <div class="value">{{ filters.type | titlecase }}</div>
      </div>
    </div>
  </header>

  <section class="panel">
    <app-filter-bar
      [filters]="filters"
      [genres]="genres"
      (filtersChange)="handleFiltersChange($event)"
    />
  </section>

  <section class="panel">
    <app-stats-panel [songs]="songs" />
  </section>

  <section class="panel">
    @if (loading) {
      <div class="loading">Loading songs...</div>
    } @else if (error) {
      <div class="error">{{ error }}</div>
    } @else if (!songs.length) {
      <div class="empty">No songs matched these filters.</div>
    } @else {
      <div class="grid">
        @for (song of songs; track song.id) {
          <app-song-card
            [song]="song"
            (selectSong)="selectSong(song)"
            (previewSong)="previewSong(song)"
          />
        }
      </div>
    }
  </section>

  @if (selectedSong) {
    <div class="modal-backdrop" (click)="selectedSong = undefined">
      <section class="panel detail modal" (click)="$event.stopPropagation()">
        <header class="modal-header">
          <div>
            <p class="eyebrow">Selected song</p>
            <h2>{{ selectedSong.title }}</h2>
            <p class="meta">
              {{ selectedSong.artist }}
              @if (selectedSong.featuredArtists.length) {
                • ft. {{ selectedSong.featuredArtists.join(', ') }}
              }
            </p>
          </div>
          <button class="ghost close" (click)="selectedSong = undefined">×</button>
        </header>
        <div class="detail-body">
          <div>
            <div class="tag-row">
              <span class="pill">{{ selectedSong.genre }}</span>
              <span class="pill subtle">{{ selectedSong.type | titlecase }}</span>
              <span class="pill subtle">Rank #{{ selectedSong.rank }}</span>
            </div>
            <p class="description">{{ selectedSong.description || 'No description yet.' }}</p>
            @if (selectedSong.previewUrl) {
              <button class="primary" (click)="previewSong(selectedSong)">Play preview</button>
            }
          </div>
          <img
            [src]="selectedSong.coverUrl || 'https://picsum.photos/seed/selected-song/400/400'"
            [alt]="selectedSong.title"
            class="detail-cover"
          />
        </div>
      </section>
    </div>
  }
</main>
